module.exports = "# Database counter
type Count {
  # Current amount
  amount: Int!
}

extend type Query {
  # Counter
  count: Count
}

extend type Mutation {
  # Increase counter value, returns current counter amount
  addCount(
    # Amount to add to counter
    amount: Int!
  ): Count
}

extend type Subscription {
  # Subscription fired when anyone increases counter
  countUpdated(ids: String): Count
}
"

module.exports = "# Post
type Post {
  id: ID!
  title: String!
  content: String!
  comments: [Comment]
}

# Comment
type Comment {
  id: ID!
  content: String!
}

# Edges for PostsQuery
type PostEdges {
  node: Post
  cursor: ID
}

# PageInfo for PostsQuery
type PostPageInfo {
  endCursor: ID
  hasNextPage: Boolean
}

# Posts relay-style pagination query
type PostsQuery {
  totalCount: Int
  edges: [PostEdges]
  pageInfo: PostPageInfo
}

extend type Query {
  # Posts pagination query
  postsQuery(limit: Int, after: ID): PostsQuery
  # Post
  post(id: ID!): Post
}

extend type Mutation {
  # Create new post
  addPost(input: AddPostInput!): Post
  # Delete a post
  deletePost(id: ID!): Post
  # Edit a post
  editPost(input: EditPostInput!): Post
  # Add comment to post
  addComment(input: AddCommentInput!): Comment
  # Delete a comment
  deleteComment(input: DeleteCommentInput!): Comment
  # Edit a comment
  editComment(input: EditCommentInput!): Comment
}

# Input for addPost Mutation
input AddPostInput {
  title: String!
  content: String!
}

# Input for editPost Mutation
input EditPostInput {
  id: ID!
  title: String!
  content: String!
}

# Input for addComment Mutation
input AddCommentInput {
  content: String!
  # Needed for commentUpdated Subscription filter
  postId: ID!
}

# Input for editComment Mutation
input DeleteCommentInput {
  id: ID!
  # Needed for commentUpdated Subscription filter
  postId: ID!
}

# Input for deleteComment Mutation
input EditCommentInput {
  id: ID!
  content: String!
  # Needed for commentUpdated Subscription filter
  postId: ID!
}

extend type Subscription {
  # Subscription for when editing a post
  postUpdated(id: ID!): Post
  # Subscription for post list
  postsUpdated(endCursor: ID!): UpdatePostPayload
  # Subscription for comments
  commentUpdated(postId: ID!): UpdateCommentPayload
}

# Payload for postsUpdated Subscription
type UpdatePostPayload {
  mutation: String!
  id: ID!
  node: Post
}

# Payload for commentUpdated Subscription
type UpdateCommentPayload {
  mutation: String!
  id: ID
  postId: ID!
  node: Comment
}
"
